#include <vector>
struct Thimbles{int thimbleWithBall(std::vector<std::string>b){int a=1,i,c,d;for(i=0;i<b.size();i++){c=b[i][0]-'0';d=b[i][2]-'0';a==c?a=d:a==d?a=c:a;}return a;}};

                                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                                              
